sections.css:
// Glyphicons font path
$icon-font-path:        "../fonts/";

// Variables
//
// This file contains variables that are used to normalize the values used in
// atom style definitions.

$eg-default-fonts: sans-serif;
$eg-spacings: 10px;

// Color palette

$eg-color-black: #00fdff;
$eg-color-orange: #00f900;
$eg-color-cyan: #00fdff;
$eg-color-brown: #ff9300;


.eg-person-info {

  font-family: $eg-default-fonts;
  border: thin solid $eg-color-black;

  .eg-person-info__name {
    font-size: 1.2em;
    background-color: $eg-color-orange;
    padding: $eg-spacings;
  }

  .eg-person-info__description {
    padding: $eg-spacings;
  }

  &.eg-person-info_role_designer {
    .eg-person-info__name {
      background-color:  $eg-color-cyan;
    }
  }

  &.eg-person-info_role_developer {
    .eg-person-info__name {
      background-color: $eg-color-brown;
    }
  }
}

.eg-grid {

  margin-left: -$eg-spacings;

  &:after {
    content: "";
    display: table;
    clear: both;
  }

  [class*='eg-grid__item-'] {
    float: left;
    padding-left: $eg-spacings;
    padding-bottom: $eg-spacings;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
  }

  .eg-grid__item-1-2 {
    width: 50%;
  }

  .eg-grid__item-1-1 {
    width: 100%;
  }
}

.eg-input {

  // default styling goes here

  &:focus {
    // focus specific definitions go here
  }
}

